package slidingwindow;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

class LongestRepeatingCharacterTest {

    @Test
    void characterReplacement() {
        assertEquals(4, new LongestRepeatingCharacter().characterReplacement("ABAB", 2));
        assertEquals(4, new LongestRepeatingCharacter().characterReplacement("AABABBA", 1));
        assertEquals(4, new LongestRepeatingCharacter().characterReplacement("ABBB", 2 ));
        assertEquals(4, new LongestRepeatingCharacter().characterReplacement("AAAA", 0 ));
        assertEquals(7864, new LongestRepeatingCharacter().characterReplacement("",7520));
    }
}